import { RangePosition } from './css-syntax-error.js'
import Node from './node.js'

declare namespace Warning {
  export interface WarningOptions {
   
    end?: RangePosition

  
    endIndex?: number

    index?: number

    node?: Node

    plugin?: string

    start?: RangePosition

   
    word?: string
  }

  export { Warning_ as default }
}


declare class Warning_ {
  
  column: number

 
  endColumn?: number

  
  endLine?: number

  
  line: number

  node: Node

  plugin: string


  text: string

  type: 'warning'

  /**
   * @param text 
   * @param opts 
   */
  constructor(text: string, opts?: Warning.WarningOptions)

  /**
  
   * @return 
   */
  toString(): string
}

declare class Warning extends Warning_ {}

export = Warning
